import React, { useState } from 'react';
import File from './file/File';
import { useSelector, useDispatch } from 'react-redux';
import { setCurrentDir, setDraggingFile } from '../../../reducers/fileReducer';
import './fileList.css';
import { updateFilePath } from '../../../actions/file';

const FileList = () => {
    const files = useSelector(state => state.files.files);
    const currentDir = useSelector(state => state.files.currentDir);
    const dirStack = useSelector(state => state.files.dirStack);
    const dispatch = useDispatch();
    function backClickHandler() {
        const backDirId = dirStack.pop();
        dispatch(setCurrentDir(backDirId));
    }
    const [draggingFile, setDraggingFile] = useState();
    const getFile = file => {
        setDraggingFile(file);
    }
    const backDropHandler = (e, draggingFile) => {
        const backDirId = dirStack.pop();
        console.log(draggingFile);
        dispatch(updateFilePath(draggingFile, backDirId));
        dispatch(setDraggingFile(null));
        dispatch(setCurrentDir(backDirId));
    }
    return (
        <div className='FileList-container'>
            <div className='FileList-header'>
                {currentDir !== 'null' ?
                    <button
                    className='FileList-back'
                    title='Back'
                    onClick={() => backClickHandler()}
                    onDrop={e => backDropHandler(e, draggingFile)}
                    draggable={true}
                    >â€¹</button>
                : ''}
                <span className='FileList-name'>Name</span>
                <span className='FileList-date'>Last modified</span>
                <span className='FileList-size'>File size</span>
            </div>
            {
                files.map(file => 
                    <File
                        getFile={getFile}
                        file={file}
                        key={file.id}
                    />)
            }
        </div>
    );
};

export default FileList;
