import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import { getFiles } from '../../actions/file';
import FileList from './fileList/FileList';
import './disk.css';
import { Dropdown, DropdownButton } from 'react-bootstrap';
import 'bootstrap';

const Disk = () => {
    const dispatch = useDispatch();
    const currentDir = useSelector(state => {
        return state.files.currentDir;
    });
    useEffect(() => {
        dispatch(getFiles(currentDir));
    }, [currentDir]);
    return (
        <div className='Disk-container'>
            <div className='Disk-btns'>
                {/* <button className='Disk-create-dir'>New</button> */}
                {/* <Dropdown>
                    <DropdownButton className='Disk-create-dir'>New</DropdownButton>
                    <Dropdown.Item onClick={() => {alert('xui')}}>Action</Dropdown.Item>
                    <Dropdown.Item >Another action</Dropdown.Item>
                    <Dropdown.Item >Something else</Dropdown.Item>
                </Dropdown> */}
                <div class="btn-group">
                    <button
                    data-toggle="dropdown"
                    aria-haspopup="true"
                    aria-expanded="false"
                    className='Disk-create-btn'
                    >
                        New
                    </button>
                    <div class="dropdown-menu">
                        <span class="dropdown-item">Folder</span>
                        <div class="dropdown-divider"></div>
                        <span class="dropdown-item">File upload</span>
                        <a class="dropdown-item">Folder upload</a>
                        <div class="dropdown-divider"></div>
                        <a class="dropdown-item">Separated link</a>
                    </div>
                </div>
            </div>
            <FileList />
        </div>
    );
}

export default Disk;
