import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import { createDir, getFiles } from '../../actions/file';
import FileList from './fileList/FileList';
import './disk.css';
import 'bootstrap';
import uploadFile from '../../assets/upload-file.png';
import uploadFolder from '../../assets/upload-folder.png';
import createFolder from '../../assets/create-folder.png';
import Popup from './Popup';

const Disk = () => {
    const dispatch = useDispatch();
    const currentDir = useSelector(state => {
        return state.files.currentDir;
    });
    useEffect(() => {
        dispatch(getFiles(currentDir));
    }, [currentDir]);
    function createDirHandler() {
        // dispatch(createDir(currentDir, 'new_dir'));
        return <Popup />
    }
    return (
        <div className='Disk-container'>
            <div className='Disk-btns'>
                <div class="btn-group">
                    <button
                    data-toggle="dropdown"
                    aria-haspopup="true"
                    aria-expanded="false"
                    className='Disk-create-btn'
                    >New</button>
                    <div class="dropdown-menu">
                        <button data-toggle='modal' class="dropdown-item" onClick={() => createDirHandler()}>
                            <img src={createFolder} alt='' />
                            Folder
                        </button>
                        <div class="dropdown-divider"></div>
                        <span class="dropdown-item">
                            <img src={uploadFile} alt='' />
                            File upload
                        </span>
                        <span class="dropdown-item">
                            <img src={uploadFolder} alt='' />
                            Folder upload
                        </span>
                    </div>
                </div>
            </div>
            <FileList />
        </div>
    );
}

export default Disk;
