import React from 'react';
import './file.css';
import folder from '../../../../assets/folder.png';
import doc from '../../../../assets/doc.png';
import pdf from '../../../../assets/pdf.png';
import picture from '../../../../assets/picture.png';
import zipFile from '../../../../assets/zip-file.png';
import txtFile from '../../../../assets/file.png';
import mp3 from '../../../../assets/mp3.png'
import { useDispatch, useSelector } from 'react-redux';
import { useState } from 'react';
import { setCurrentDir, pushToStack, setDraggingFile, removeFromFiles } from '../../../../reducers/fileReducer';
import { updateFilePath } from '../../../../actions/file';

const File = ({file}) => {
    const type =
                file.type === 'dir' ? folder :
                file.type === 'doc' ? doc :
                file.type === 'pdf' ? pdf :
                file.type === 'png' ? picture :
                file.type === 'jpg' ? picture :
                file.type === 'jpeg' ? picture :
                file.type === 'zip' ? zipFile :
                file.type === 'mp3' ? mp3 :
                txtFile;
    const dispatch = useDispatch();
    const currentDir = useSelector(state => state.files.currentDir);
    const draggingFile = useSelector(state => state.files.draggingFile);
    function openDirHandler(file) {
        if(file.type === 'dir') {
            dispatch(pushToStack(currentDir));
            dispatch(setCurrentDir(file._id));
        }
        // localStorage.setItem('currentDirId', file._id);
    }
    const focusHandler = e => {
        e.target.className = '.File-container-hover';
    }
    const blurHandler = e => {
        e.target.className = '.File-container';
    }
    const dragStartHandler = (e, file) => {
        dispatch(setDraggingFile(file));
    }
    const dragLeaveHandler = e => {
        e.target.style.background = 'white';
    }
    const dragEndHandler = e => {
        e.target.style.background = 'white';
    }
    const dragOverHandler = (e, file) => {
        e.preventDefault();
        if(file.type === 'dir') {
            e.target.style.background = '#f0f0f0';
        }
    }
    const dropHandler = (e, file) => {
        e.preventDefault();
        if(file.type === 'dir') {
            dispatch(updateFilePath(draggingFile, file));
            dispatch(setDraggingFile(null));
            dispatch(removeFromFiles(draggingFile));
        }
    }
    return (
        <div
            tabIndex={1}
            className='File-container'
            onFocus={(e) => focusHandler(e)}
            onBlur={(e) => blurHandler(e)}
            onDoubleClick={() => openDirHandler(file)}
            onDragStart={(e) => dragStartHandler(e, file)}
            onDragLeave={(e) => dragLeaveHandler(e)}
            onDragEnd={(e) => dragEndHandler(e)}
            onDragOver={(e) => dragOverHandler(e, file)}
            onDrop={(e) => dropHandler(e, file)}
            draggable={true}
        >
            <input id='1' style={{display: 'none'}} />
            {/* <div className='File-section'></div> */}
            <img className='File-img' src={type} alt='' />
            <span className='File-name'>{file.name}</span>
            <span className='File-date'>{file.updatedAt.split('T')[0]}</span>
            <span className='File-size'>{file.size}</span>
        </div>
    )
}

export default File;
