import axios from "axios";
import { addFolder, setFiles } from "../reducers/fileReducer";
import { useSelector } from "react-redux";

export function getFiles(dirId) {
    return async dispatch => {
        // dirId = dirId === null ? userId : dirId;
        const res = await axios.get(`http://localhost:5000/api/drive/file/${dirId}`, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            }
        });
        dispatch(setFiles(res.data));
    }
}

export function createDir(dirId, dirName) {
    return async dispatch => {
        // const userId = useSelector(state => state.user.currentUser.userId);
        // dirId = dirId === null ? userId : dirId;
        const res = await axios.post(`http://localhost:5000/api/drive/folder`, {
            name: dirName,
            parentId: dirId,
            type: 'dir'
        },
        {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            }
        });
        dispatch(addFolder(res.data));
    }
}
