import axios from 'axios';
import { setUser } from '../reducers/userReducer';

export const user = () => {
    return async dispatch => {
        const res = await axios.post('http://localhost:5000/api/auth/check');
        const {user} = res.data;
        console.log(user.userId);
        dispatch(setUser(user))
    }
};
