import React from 'react';
import './file.css';
import folder from '../../../../assets/folder.png';
import doc from '../../../../assets/doc.png';
import pdf from '../../../../assets/pdf.png';
import picture from '../../../../assets/picture.png';
import zipFile from '../../../../assets/zip-file.png';
import txtFile from '../../../../assets/file.png';
import mp3 from '../../../../assets/mp3.png'
import { useDispatch, useSelector } from 'react-redux';
import { setCurrentDir, pushToStack } from '../../../../reducers/fileReducer';

const File = ({file}) => {
    const type =
                file.type === 'dir' ? folder :
                file.type === 'doc' ? doc :
                file.type === 'pdf' ? pdf :
                file.type === 'png' ? picture :
                file.type === 'zip' ? zipFile :
                file.type === 'mp3' ? mp3 :
                txtFile;
    const dispatch = useDispatch();
    const currentDir = useSelector(state => state.files.currentDir);
    function openDirHandler(file) {
        if(file.type === 'dir') {
            dispatch(pushToStack(currentDir));
            dispatch(setCurrentDir(file._id));
        }
        // localStorage.setItem('currentDirId', file._id);
    }
    const dragStartHandler = (e, file) => {
        console.log(file);
    }
    const dragLeaveHandler = e => {
        e.target.style.background = 'white';
    }
    const dragEndHandler = e => {
        e.target.style.background = 'white';
    }
    const dragOverHandler = e => {
        e.preventDefault();
        e.target.style.background = '#f0f0f0';
    }
    const dropHandler = (e, file) => {
        e.preventDefault();
        console.log(file);
    }
    return (
        <div className='File-section'>
            <div
                className='File-container'
                onDoubleClick={() => openDirHandler(file)}
                onDragEnter={(e) => dragStartHandler(e, file)}
                onDragLeave={(e) => dragLeaveHandler(e)}
                onDragEnd={(e) => dragEndHandler(e)}
                onDragOver={(e) => dragOverHandler(e)}
                onDrop={(e) => dropHandler(e, file)}
                draggable={true}
            >
                <img className='File-img' src={type} alt='' />
                <span className='File-name'>{file.name}</span>
                <span className='File-date'>{file.updatedAt.split('T')[0]}</span>
                <span className='File-size'>{file.size}</span>
            </div>
        </div>
    )
}

export default File;
