import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import { createDir, getFiles } from '../../actions/file';
import FileList from './fileList/FileList';
import './disk.css';
import 'bootstrap';
import uploadFile from '../../assets/upload-file.png';
import uploadFolder from '../../assets/upload-folder.png';
import createFolder from '../../assets/create-folder.png';
import Popup from './Popup';

const Disk = () => {
    const dispatch = useDispatch();
    const currentDir = useSelector(state => state.files.currentDir);
    useEffect(() => {
        // const currentDirId = localStorage.getItem('currentDirId') || currentDir;
        dispatch(getFiles(currentDir));
    }, [currentDir]);
    return (
        <div className='Disk-container'>
            <div className='Disk-btns'>
                <div class="btn-group">
                    <button
                    data-toggle="dropdown"
                    aria-haspopup="true"
                    aria-expanded="false"
                    className='Disk-create-btn'
                    >New</button>
                    <div class="dropdown-menu">
                        <button data-toggle='modal' class="dropdown-item" data-target="#exampleModal">
                            <img src={createFolder} alt='' />
                            Folder
                        </button>
                        <div class="dropdown-divider"></div>
                        <label class="dropdown-item" htmlFor='Disk-upload_file'>
                            <img src={uploadFile} alt='' />
                            File upload
                        </label>
                        <input
                            type='file'
                            id='Disk-upload_file'
                            className='Disk-upload_file'
                            multiple={true}
