import axios from "axios";
import {
    addFile,
    setFiles,
    removeFromFiles,
    renameFileReducer,
    updateFile
} from "../reducers/fileReducer";

export function getFiles(dirId) {
    return async dispatch => {
        const res = await axios.get(`http://localhost:5000/api/drive/file/${dirId ? dirId : ''}`, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            }
        });
        dispatch(setFiles(res.data));
    }
}

export function createDir(dirId, dirName) {
    return async dispatch => {
        const res = await axios.post(`http://localhost:5000/api/drive/folder`, {
            name: dirName,
            parentId: dirId,
            type: 'dir'
        },
        {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            }
        });
        if(res.data.message) {
            return res.data.message;
        }
        dispatch(addFile(res.data));
        return res.data;
    }
}

export function uploadFile(dirId, file) {
    return async dispatch => {
        const data = new FormData();
        data.append('file', file);
        data.append('parentId', dirId);
        const res = await axios.post('http://localhost:5000/api/drive/file', data, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            },
            onUploadProgress: progressEvent => {
                const totalLength = progressEvent.lengthComputable ? progressEvent.total : progressEvent.target.getResponseHeader('content-length') || progressEvent.target.getResponseHeader('x-decompressed-content-length');
                console.log(totalLength)
                if (totalLength) {
                    let progress = Math.round((progressEvent.loaded * 100) / totalLength)
                    console.log(progress)
                }
            }
        });
        if(res.data.message) {
            return res.data.message;
        }
        dispatch(addFile(res.data));
    }
}

export function updateFilePath(childFile, parentFile) {
    return async dispatch => {
        const data = {
            childFile,
            parentFile
        };
        const res = await axios.put('http://localhost:5000/api/drive/file/move', data, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            }
        });
        dispatch(removeFromFiles(childFile));
        // dispatch(updateFile(res.data));
        dispatch(getFiles(childFile.parentId));
    }
}

