import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import { createDir, getFiles } from '../../actions/file';
import FileList from './fileList/FileList';
import './disk.css';
import 'bootstrap';
import uploadFile from '../../assets/upload-file.png';
import uploadFolder from '../../assets/upload-folder.png';
import createFolder from '../../assets/create-folder.png';

const Disk = () => {
    const dispatch = useDispatch();
    const currentDir = useSelector(state => {
        return state.files.currentDir;
    });
    useEffect(() => {
        dispatch(getFiles(currentDir));
    }, [currentDir]);
    function createDirHandler() {
        // dispatch(createDir(currentDir, 'new_dir'));
        return (
            <div class="modal" tabindex="-1" role="dialog">
                <div class="modal-dialog" role="document">
                <div class="modal-content">
                    <div class="modal-header">
                    <h5 class="modal-title">Modal title</h5>
                    <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                    </button>
                    </div>
                    <div class="modal-body">
                    <p>Modal body text goes here.</p>
                    </div>
                    <div class="modal-footer">
                    <button type="button" class="btn btn-primary">Save changes</button>
                    <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                    </div>
                </div>
                </div>
            </div>
        );
    }
    return (
        <div className='Disk-container'>
            <div className='Disk-btns'>
                <div class="btn-group">
                    <button
                    data-toggle="dropdown"
                    aria-haspopup="true"
                    aria-expanded="false"
                    className='Disk-create-btn'
                    >New</button>
                    <div class="dropdown-menu">
                        <span class="dropdown-item" onClick={() => createDirHandler()}>
                            <img src={createFolder} alt='' />
                            Folder
                        </span>
                        <div class="dropdown-divider"></div>
                        <span class="dropdown-item">
                            <img src={uploadFile} alt='' />
                            File upload
                        </span>
                        <span class="dropdown-item">
                            <img src={uploadFolder} alt='' />
                            Folder upload
                        </span>
                    </div>
                </div>
            </div>
            <FileList />
        </div>
    );
}

export default Disk;
