import React from 'react';
import File from './file/File';
import { useSelector, useDispatch } from 'react-redux';
import { setCurrentDir } from '../../../reducers/fileReducer';
import './fileList.css';

const FileList = () => {
    const files = useSelector(state => state.files.files).map(file => 
        <File
            backDragOverHandler={backDragOverHandler}
            file={file}
            key={file.id}
        />
    );
    const currentDir = useSelector(state => state.files.currentDir);
    const dirStack = useSelector(state => state.files.dirStack);
    const dispatch = useDispatch();
    function backClickHandler() {
        const backDirId = dirStack.pop();
        dispatch(setCurrentDir(backDirId));
    }
    return (
        <div className='FileList-container'>
            <div className='FileList-header'>
                {currentDir !== 'null' ?
                    <button
                    className='FileList-back'
                    title='Back'
                    onClick={() => backClickHandler()}
                    onDragOver={e => backDragOverHandler(e)}
                    >â€¹</button>
                : ''}
                <span className='FileList-name'>Name</span>
                <span className='FileList-date'>Last modified</span>
                <span className='FileList-size'>File size</span>
            </div>
            {files}
        </div>
    );
};

export default FileList;
