const SET_USER = 'SET_USER';

const defaultState = {
    currentUser: {},
    isAuth: false
}

export default function userReducer(state = defaultState, action) {
    switch(action.type) {
        case SET_USER:
            return {
                ...state,
                currentUser: action.payload.user,
                isAuth: true
            }
        default:
            return state;
    }
}

const setUser = user => {
    return {
        type: SET_USER,
        payload: user
    }
}
