import { useSelector } from "react-redux";

const SET_FILES = 'SET_FILES';
const SET_CURRENT_DIR = 'SET_CURRENT_DIR';
const ADD_FOLDER = 'ADD_FOLDER';

const UserId = () => {
    return useSelector(state => state.user.currentUser.userId);
}
const defaultState = {
    files: [],
    currentDir: UserId
}

export default function fileReducer(state = defaultState, action) {
    switch(action.type) {
        case SET_FILES:
            return {
                ...state,
                files: action.payload
            }
        case SET_FILES:
            return {
                ...state,
                currentDir: action.payload
            }
        case ADD_FOLDER:
            return {
                ...state,
                files: [
                    ...state.files,
                    action.payload
                ]
            }
        default:
            return state;
    }
}

export const setFiles = files => {
    return {
        type: SET_FILES,
        payload: files
    }
}
export const setCurrentDir = dir => {
    return {
        type: SET_CURRENT_DIR,
        payload: dir
    }
}
export const addFolder = dir => {
    return {
        type: ADD_FOLDER,
        payload: dir
    }
}
