import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import { getFiles, uploadFile, createDir } from '../../actions/file';
import { setCurrentDir } from '../../reducers/fileReducer';
import FileList from './fileList/FileList';
import './disk.css';
import 'bootstrap';
import uploadFileLogo from '../../assets/upload-file.png';
import uploadFolderLogo from '../../assets/upload-folder.png';
import createFolderLogo from '../../assets/create-folder.png';
import Popup from './Popup';

const Disk = () => {
    const dispatch = useDispatch();
    let currentDir = useSelector(state => state.files.currentDir);
    const files = useSelector(state => state.files.files);
    const userId = useSelector(state => state.user.currentUser.userId);
    useEffect(() => {
        // const currentDirId = localStorage.getItem('currentDirId') || currentDir;
        dispatch(getFiles(currentDir));
        document.addEventListener('scroll', scrollHandler);
    }, [currentDir]);
    function fileUploadHandler(e) {
        currentDir = currentDir === null ? userId : currentDir;
        const files = [...e.target.files];
        files.forEach(file => dispatch(uploadFile(currentDir, file)));
    }
    function folderUploadHandler(e) {
        currentDir = currentDir === null ? userId : currentDir;
        const files = [...e.target.files];
        files.forEach(file => {
            const path = file.webkitRelativePath.split('/');
            const fileName = path.pop();
            function createTreeDir(dir) {
                path.forEach(async folder => {
                    const res = await dispatch(createDir(currentDir, folder));
                    console.log(res);
                });
            }
            // path.map(folder => {
            //     const res = await dispatch(createDir(currentDir, folder));
            //     console.log(res);
            // })
        });
        // folder.forEach(file => {
        //     const pathArray = file.webkitRelativePath.split('/');
        //     pathArray.map(dir => {
        //         if(dir == pathArray[-1]) {
        //             return dispatch(uploadFile(files[name], file));
        //         }
        //         dispatch(createDir(currentDir, dir);
        //     })
            // dispatch(createDir(currentDir, file.webkitRelativePath.split('/').shift()));
            // dispatch(setCurrentDir(files.pop()._id));
            // const newFolder = files.pop().type == 'dir' ? files.pop()._id : currentDir;
            // dispatch(uploadFile(newFolder, file));
        // });
    }
    const scrollHandler = () => {
        const scroll = document.documentElement.scrollTop;
        const button = document.getElementById('Disk-create-btn').style;
        if(scroll >= 60) {
            button.transition = '.0s';
            button.marginTop = '-60px';
            button.position = 'fixed';
            setTimeout(() => {
                button.transition = '.3s';
            }, 5);
        }
        if(scroll <= 60) {
            button.transition = '.0s';
            button.marginTop = '0px';
            button.position = '';
            setTimeout(() => {
                button.transition = '.3s';
            }, 5);
        }
    }
    return (
        <div className='Disk-container'>
            <div className='Disk-btns'>
                <div className="btn-group">
                    <button
                    data-toggle="dropdown"
                    aria-haspopup="true"
                    aria-expanded="false"
                    className='Disk-create-btn'
                    id='Disk-create-btn'
                    >New</button>
                    <div className="dropdown-menu">
                        <button data-toggle='modal' className="dropdown-item" data-target="#exampleModal">
                            <img src={createFolderLogo} alt='' />
                            Folder
                        </button>
                        <div className="dropdown-divider"></div>
                        <label className="dropdown-item" htmlFor='Disk-upload_file'>
                            <img src={uploadFileLogo} alt='' />
                            File upload
                        </label>
                        <input
                            type='file'
                            id='Disk-upload_file'
                            className='Disk-upload_file'
                            multiple={true}
                            onChange={(e) => fileUploadHandler(e)}
                            />
                        <label className="dropdown-item" htmlFor='Disk-upload_folder'>
                            <img src={uploadFolderLogo} alt='' />
                            Folder upload
                        </label>
                        <input
                            type='file'
                            id='Disk-upload_folder'
                            className='Disk-upload_folder'
                            webkitdirectory='true'
                            mozdirectory='true'
                            directory='true'
                            onChange={(e) => folderUploadHandler(e)}
                        />
                    </div>
                </div>
            </div>
            <FileList />
            <Popup />
        </div>
    );
}

export default Disk;
